import React, { useState, useEffect } from "react";
import { Row, Col, Button} from 'react-bootstrap';


function FeedCharacters() {
    const [searchText, setSearchText] = useState("");
    const [results, setResults] = useState([]);



    useEffect(() => {
      if (searchText) {
        fetch(`https://rickandmortyapi.com/api/character/?name=${searchText}`)
          .then((response) => response.json())
          .then((data) => {
            setResults(data.results);
          })
          .catch((error) => console.log(error));
      } else {
        // Si no hay un filtro de búsqueda, obtén todos los personajes en la página 1
        fetch("https://rickandmortyapi.com/api/character?page=1")
          .then((response) => response.json())
          .then((data) => {
            setResults(data.results);
          })
          .catch((error) => console.log(error));
      }
    }, [searchText]);

    const handleInputChange = (event) => {
      setSearchText(event.target.value);
    };

  return (
    <div>
    <Row className="justify-content-md-left">
      <Col>
        <input
          type="text"
          placeholder="Choose your favourite Character"
          className="Buscador"
          value={searchText}
          onChange={handleInputChange}
        /> 

      </Col>
    </Row>

    <h2 className="titles">
      - <span className="whiteTitle">Select your character</span> -
    </h2>

    <Row>
      {results.map((character) => (
        <Col xs={6} md={3} key={character.id}>
         <p>{character.residents}</p>
        </Col>
      ))}
    </Row>
  </div>
  );




}

export default FeedCharacters;
