import React, {useState, useEffect} from 'react';
import {Row, Col} from 'react-bootstrap';


function FeedEpisodes() {

    const [results, setResults] = useState([]);



    useEffect(() => {

        fetch(`https://rickandmortyapi.com/api/episode/`)
          .then((response) => response.json())
          .then((data) => {
            setResults(data.results);
          })
          .catch((error) => console.log(error));
      } 
    );



    return(
        <div>
        <Row className="justify-content-md-left">
        <Col>
          <input
            type="text"
            placeholder="Choose your favourite Character"
            className="Buscador"
            value={searchText}
            onChange={handleInputChange}
          /> 

        </Col>
      </Row>

      <h2 className="titles">
        - <span className="whiteTitle">Select your character</span> -
      </h2>

      <Row>
        {results.map((character) => (
          <Col xs={6} md={3} key={character.id}>
            <a href={`/character/${character.id}`}>
              <div className="card">
                <img src={character.image} alt={character.name} />
                <p>
                  <div className="StatusGreen"> </div>
                  {character.name}
                </p>
              </div>
            </a>
          </Col>
        ))}
      </Row>
      </div>
    )
}

export default FeedEpisodes;
